let baseTemp;
switch (month) {
    case 1: case 2: case 12:
        baseTemp = 27; // mùa "mát"
        break;
    case 3: case 4: case 5:
        baseTemp = 29; // mùa nắng đỉnh điểm
        break;
    case 6: case 7: case 8: case 9: case 10: case 11:
        baseTemp = 28; // mùa mưa
        break;
    default:
        baseTemp = 28;
}

let temp = (Math.random() * 1.5 + baseTemp).toFixed(1);

msg.payload = `🌡️ Nhiệt độ: ${temp} °C – 🏙️ TP.HCM`;
return msg;

