let now = new Date();

// Cộng thêm 7 tiếng để ra giờ Việt Nam
now.setHours(now.getHours() + 7);

let gio = now.getHours().toString().padStart(2, '0');
let phut = now.getMinutes().toString().padStart(2, '0');
let giay = now.getSeconds().toString().padStart(2, '0');

let ngay = now.getDate().toString().padStart(2, '0');
let thang = (now.getMonth() + 1).toString().padStart(2, '0');
let nam = now.getFullYear();

msg.payload = `🕒 ${gio}:${phut}:${giay} | 📅 ${ngay}/${thang}/${nam}`;
return msg;

// Parse token
const data = JSON.parse(msg.payload);

const currentToken = flow.get("currentToken");
const tokenTimestamp = flow.get("tokenTimestamp");
const now = new Date().getTime();
const threeMinutes = 10 * 60 * 1000;

if (data.token) {
    if (!currentToken || (now - tokenTimestamp > threeMinutes) || data.token !== currentToken) {
        flow.set("currentToken", data.token);
        flow.set("tokenTimestamp", now);
        flow.set("allow", true);
    } else {
        flow.set("allow", true);
    }
} else {
    flow.set("allow", false);
}

return null;
